mapper.select:

select * from (
	select rownum rnum, TT.* from (
		select * from QABOARD2 WHERE Q_USERID like '%'||#{id}||'%' order by Q_IDX asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}


select * from (
	select rownum rnum, TT.* from (
		select * from REVIEW WHERE RE_USERID = #{id} order by RE_IDX asc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}
